    border-radius:20px;
    margin-top:90px;
    color:#fff;
    justify-content:space-between;
}
.distributer-content{
   justify-content: center;
    display: flex;
    flex-direction: column;
    padding-top:70px;
    padding-bottom:70px;
}
.distributer-title{
    font-size:28px;
    font-weight:bold;
}
.distributer-description{
    font-weight:400;
    font-size:20px;
